import React, { Component } from "react";
import "./App.css";
import TaskForm from "./components/TaskForm";
import Controls from "./components/Controls";
import TaskList from "./components/TaskList";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { tasks: [], isDisplayForm: false };
  }

  // Keep tasks when reload page
  componentWillMount() {
    if (localStorage && localStorage.getItem("tasks")) {
      var tasks = JSON.parse(localStorage.getItem("tasks"));
      this.setState({ tasks: tasks });
    }
  }

  // Generate sample tasks
  handleGenerateData = () => {
    var tasks = [
      { id: this.generateID(), name: "sampleA", status: true },
      { id: this.generateID(), name: "sampleA", status: true },
      { id: this.generateID(), name: "sampleA", status: true },
    ];
    this.setState({
      tasks: tasks,
    });
    localStorage.setItem("tasks", JSON.stringify(tasks));
  };

  handleToggleForm = () => {
    this.setState({
      isDisplayForm: !this.state.isDisplayForm,
    });
  };

  handleCloseForm = () => {
    this.setState({
      isDisplayForm: false,
    });
  };

  handleSubmit = (data) => {
    var { tasks } = this.state;
    data.id = this.generateID();
    tasks.push(data);
    this.setState({
      tasks: tasks,
    });
    localStorage.setItem("tasks", JSON.stringify(tasks));
  };

  // Create a random string
  s4() {
    return Math.floor((1 + Math.random()) * 0x10000)
      .toString(16)
      .substring(1);
  }

  // Generate an unique ID from random strings
  generateID() {
    return (
      this.s4() +
      this.s4() +
      "-" +
      this.s4() +
      "-" +
      this.s4() +
      "-" +
      this.s4() +
      "-" +
      this.s4() +
      "-" +
      this.s4() +
      "-" +
      this.s4()
    );
  }

  render() {
    var { tasks, isDisplayForm } = this.state; // var tasks = this.state.tasks
    var elementTaskForm = isDisplayForm ? (
      <TaskForm
        onSubmit={this.handleSubmit}
        onCloseForm={this.handleCloseForm}
      />
    ) : (
      ""
    );
    return (
      <div className="container">
        <div className="text-center">
          <h1>TODO MANAGEMENT</h1>
          <hr />
        </div>
        <div className="row">
          <div
            className={
              isDisplayForm === true
                ? "col-xs-4 col-sm-4 col-md-4 col-lg-4"
                : ""
            }
          >
            {/* Form */}
            {elementTaskForm}
          </div>
          <div
            className={
              isDisplayForm === true
                ? "col-xs-8 col-sm-8 col-md-8 col-lg-8"
                : "col-xs-12 col-sm-12 col-md-12 col-lg-12"
            }
          >
            <button
              type="button"
              className="btn btn-primary"
              onClick={this.handleToggleForm}
            >
              <span className="fa fa-plus mr-5"></span>Add TODO
            </button>
            <button
              type="button"
              className="btn btn-danger ml-5"
              onClick={this.handleGenerateData}
            >
              Generate Data
            </button>
            {/* Controls */}
            <Controls />
            {/* List */}
            <div className="row mt-15">
              <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                <TaskList tasks={tasks} />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
